mapper.insert:
MERGE INTO DEPT a 
USING DUAL
ON (a.d_code = #{d_code}
    or a.d_name = #{d_name}
    or #{d_code} NOT LIKE 'D%'
   ) 
WHEN NOT MATCHED THEN
	INSERT (a.d_code,a.d_name)
	VALUES (#{d_code},#{d_name})

mapper.select:
SELECT d_code,
	   d_name
FROM DEPT
WHERE 1=1
AND d_code = #{d_code}
AND d_name = #{d_name}

mapper.update:
		MERGE INTO DEPT A
		USING (SELECT D_CODE,D_NAME 
	           FROM DEPT
	           WHERE D_CODE = #{d_code}
          ) B
		ON ( A.d_code = B.D_CODE
		   ) 
   		WHEN MATCHED THEN
        	UPDATE SET A.d_name=#{d_name}

mapper.select:
SELECT w_name,
	   w_except
FROM WORK
WHERE 1=1
AND w_name = #{w_name}
AND w_except = #{w_except}

mapper.delete:
MERGE INTO WORK A
  USING DUAL
  ON (A.W_NAME = #{w_name})
  WHEN MATCHED THEN
    UPDATE SET A.W_EXCEPT = 'D'
    WHERE A.W_NAME = #{w_name}
    AND A.W_EXCEPT = #{w_except}
    DELETE WHERE A.W_NAME = #{w_name}
    AND A.W_EXCEPT = #{w_except}
  WHEN NOT MATCHED THEN
    INSERT(A.W_NAME,A.W_EXCEPT)
    VALUES ('E99','ERROR')

mapper.select:
SELECT l_code, 
	   l_name
FROM SITE
WHERE 1=1
AND l_code = #{l_code}
AND l_name = #{l_name}

mapper.update:
		MERGE INTO SITE A
		USING (SELECT L_CODE,L_NAME 
	           FROM SITE
	           WHERE L_CODE = #{l_code}
          ) B
		ON ( A.l_code = B.l_code
		   ) 
   		WHEN MATCHED THEN
        	UPDATE SET A.l_name=#{l_name}

mapper.select:
SELECT p_name,
	   p_addr
FROM PLACE
WHERE p_name = #{p_name}
AND p_addr = #{p_addr}

mapper.update:
		MERGE INTO PLACE A
		USING (SELECT P_NAME,P_ADDR 
	           FROM PLACE
	           WHERE P_NAME = #{p_name}
          ) B
		ON ( A.p_name = B.p_name
		   ) 
   		WHEN MATCHED THEN
        	UPDATE SET A.p_addr= #{p_addr}

mapper.delete:
MERGE INTO PLACE A
 USING DUAL
 ON (A.P_NAME = #{p_name})
 WHEN MATCHED THEN
   UPDATE SET A.P_ADDR = 'D'
   WHERE A.P_NAME = #{p_name}
   AND A.P_ADDR = #{p_addr}
   DELETE WHERE A.P_NAME = #{p_name}
   AND A.P_ADDR = #{p_addr}
 WHEN NOT MATCHED THEN
   INSERT(A.P_NAME,A.P_ADDR)
   VALUES ('E99','ERROR')

	 MERGE INTO MEMBER A
		  USING DUAL
		  ON (A.M_ID = #{m_id}
      AND A.M_NAME = #{m_name}
      AND A.D_CODE = #{d_code}
      AND A.M_POSITION = #{m_position})
		  WHEN MATCHED THEN
		    UPDATE SET A.M_NOTICE = 'D'
		    WHERE A.M_ID = #{m_id}
      AND A.M_NAME = #{m_name}
      AND A.D_CODE = #{d_code}
      AND A.M_POSITION = #{m_position}
		    DELETE WHERE A.M_ID = #{m_id}
      AND A.M_NAME = #{m_name}
      AND A.D_CODE = #{d_code}
      AND A.M_POSITION = #{m_position}
		  WHEN NOT MATCHED THEN
		    INSERT(A.M_ID,
               A.M_NAME,
               A.D_CODE,
               A.M_POSITION)
      VALUES ('E99','ERROR','E99','ERROR')
